sp_widget.script:
(function() {

	var result = (new PmProjectStatusReport()).statusByReportName("otherStatus", $sp.getParameter('sysparm_sys_id'), $sp.getParameter('sysparm_status_date'), $sp.getParameter('sysparm_report_id'));
	//gs.info(new JSON().encode(result));
	if(JSUtil.notNil(result)) {
		for(var attr in result) {
			data[attr] = result[attr];
		}
	}
})();
