sys_ui_policy.script_true:
function onCondition() {

//Sets the background and font color based on the value of the column - APayze - 02/19/2019

if (g_form.getValue('status_test')  ==  'Green' )  {
	g_form.getElement("status_test").style.backgroundColor="green";
	g_form.getElement("status_test").style.color="white";
	g_form.getElement("status_test").style.fontWeight ="bold";
   }

else if (g_form.getValue('status_test')  == 'Yellow' ) {
	g_form.getElement("status_test").style.backgroundColor="yellow";
	g_form.getElement("status_test").style.color="black";
	g_form.getElement("status_test").style.fontWeight ="bold";
   }

else if (g_form.getValue('status_test')  == 'Red' ) {
	g_form.getElement("status_test").style.backgroundColor="red";
	g_form.getElement("status_test").style.color="white";
	g_form.getElement("status_test").style.fontWeight ="bold";
   }
}
